SELECT StatsMsgSourceCountsID      ,StatDate      ,MSG.Name AS LogSource	  ,MSGTYPE.Name AS LogSourceType      ,MEDIATOR.Name AS DataProcess      ,SM.Name AS SysMon      ,ENTITY.Name AS Entity      ,CountLogs      ,CountIdentifiedLogs      ,CountArchivedLogs         ,CountEvents      ,CountOnlineLogs      ,CountProcessedLogs      ,CountEventsForwarded      ,CountLogMart      ,CountAnalyzedLogs  FROM LogRhythm_LogMart.dbo.StatsMsgSourceCountsMinute STATS        INNER JOIN LogRhythmEMDB.dbo.Mediator MEDIATOR				ON STATS.MediatorID = MEDIATOR.MediatorID		INNER JOIN LogRhythmEMDB.dbo.MsgSource MSG			ON STATS.MsgSourceID = MSG.MsgSourceID		 INNER JOIN LogRhythmEMDB.dbo.systemmonitor SM                ON MSG.SystemMonitorID = SM.SystemMonitorID		INNER JOIN LogRhythmEMDB.dbo.Host HOST	ON HOST.HostID = SM.HostID		INNER JOIN LogRhythmEMDB.dbo.Entity ENTITY			ON ENTITY.EntityID = Host.EntityID		INNER JOIN LogRhythmEMDB.dbo.MsgSourceType MSGTYPE		    ON MSG.MsgSourceTypeID = MSGTYPE.MsgSourceTypeID		WHERE StatDate <= DATEADD(hour, -1 , GETUTCDATE()) AND StatDate >= DATEADD(hour, -2 , GETUTCDATE()) 	ORDER BY StatsMsgSourceCountsID ASC